mapper.select:
SELECT
	COUNT(*) cnt
FROM
	fallowtab

	
		AND fallow_no = ${fallow_no}
	
	
		AND fallower_no = ${fallower_no}

SELECT fallow_no, fallower_no, id, save_name sname, save_loc sloc, ischeck FROM membertab mt, profilepictab ppt, fallowtab ft WHERE mt.mno = ppt.mno AND ft.ischeck = 'Y' AND mt.isshow = 'Y' AND mt.mno =
fallow_no AND fallower_no = ${fallower_no}
fallower_no AND fallow_no = ${fallow_no}
