mapper.select:
SELECT
   mg.*
FROM
    (SELECT
        bno, bname, gname, writer, trans, largeimg, publish
    FROM    
        booktab b, genretab g, publishtab pb
    WHERE
        b.genre = g.genre
        AND b.genre = #{genre}
        AND b.publish_no = pb.publish_no) mg
WHERE
    bname LIKE #{keyword}
    OR writer LIKE #{keyword}
    OR trans LIKE #{keyword}
    OR publish LIKE #{keyword}
